import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Sidebar = ({ role }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    // In a real app, clear auth tokens here
    navigate('/');
  };

  const isActive = (path) => location.pathname === path;

  // Define links for each role
  const getLinks = () => {
    switch (role) {
      case 'participant':
        return [
          { icon: 'üè†', label: 'Dashboard', path: '/dashboard/participant' },
          { icon: 'üöÄ', label: 'Join Hackathon', path: '/dashboard/participant/internal-hackathons' },
          { icon: 'üë§', label: 'My Profile', path: '/dashboard/participant/profile' },
          { icon: 'üîñ', label: 'Bookmarks', path: '/dashboard/participant/bookmarks' },
          { icon: 'üåç', label: 'External Hackathons', path: '/dashboard/participant/external' },
        ];
      case 'organizer':
        return [
          { icon: 'üìä', label: 'Dashboard Home', path: '/dashboard/organizer' },
          { icon: 'üöÄ', label: 'My Hackathons', path: '/dashboard/organizer/hackathons' },
          { icon: '‚ûï', label: 'Create Hackathon', path: '/dashboard/organizer/create-hackathon' },
          { icon: 'üë•', label: 'Participants', path: '/dashboard/organizer/participants' },
          { icon: 'üì•', label: 'Submissions', path: '/dashboard/organizer/submissions' },
          { icon: '‚öñÔ∏è', label: 'Evaluations', path: '/dashboard/organizer/evaluations' },
          { icon: 'üì¢', label: 'Announcements', path: '/dashboard/organizer/announcements' },
          { icon: 'üë§', label: 'Profile', path: '/dashboard/organizer/profile' },
        ];
      case 'judge':
        return [
          { icon: 'üè†', label: 'Dashboard Home', path: '/dashboard/judge' },
          { icon: 'üìã', label: 'Assigned Hackathons', path: '/dashboard/judge/hackathons' },
          { icon: '‚öñÔ∏è', label: 'Pending Evaluations', path: '/dashboard/judge/evaluations' },
          { icon: '‚úÖ', label: 'Completed', path: '/dashboard/judge/completed' },
          { icon: 'üë§', label: 'Profile', path: '/dashboard/judge/profile' },
        ];
      case 'admin':
        return [
          { icon: 'üõ†Ô∏è', label: 'Control Panel', path: '/dashboard/admin' },
          { icon: 'üë•', label: 'Users', path: '/dashboard/admin/users' },
          { icon: 'üìÖ', label: 'Hackathons', path: '/dashboard/admin/hackathons' },
        ];
      default:
        return [];
    }
  };

  const links = getLinks();

  return (
    <aside className="dashboard-sidebar">
      <div className="sidebar-header">
        <a href="/" className="sidebar-brand">
          üöÄ Hackaplace
        </a>
        <span className="user-role-badge">{role}</span>
      </div>

      <nav className="sidebar-nav">
        {links.map((link, index) => (
          <div 
            key={index}
            className={`nav-item ${isActive(link.path) ? 'active' : ''}`}
            onClick={() => navigate(link.path)}
          >
            <span className="nav-icon">{link.icon}</span>
            <span>{link.label}</span>
          </div>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button className="logout-btn" onClick={handleLogout}>
          <span className="nav-icon">üö™</span>
          <span>Logout</span>
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
